import React, { useEffect, useState } from "react";

const Table = ({modelIds}) => {
  const [data, setData] = useState([]);
  const [restructuredData, setRestructuredData] = useState({});
  const dates = Array.from({ length: 30 }, (_, i) => {
    const day = i + 1;
    const formattedDay = day < 10 ? `0${day}` : day;
    return `2024-04-${formattedDay}`;
  });

  useEffect(() => {
    fetch('http://localhost:8082/data')
      .then(response => response.json())
      .then(data => {
        setData(data.data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  useEffect(() => {
    const restructureData = {};
    data.forEach(item => {
      const { modelId, date, productionValue } = item;
      if (!restructureData[modelId]) {
        restructureData[modelId] = {};
      }
      restructureData[modelId][date] = productionValue;
    });
    setRestructuredData(restructureData);
  }, [data]);

  return (
    <div className="overflow-x-auto">
      <table className="table-auto border border-collapse border-gray-500">
        <thead>
          <tr className="bg-gray-200">
            <th colSpan={dates.length + 1} className="text-center">
              <span className="mx-8">Monthly data for April</span>
            </th>
          </tr>
          <tr className="bg-gray-200">
            <th className="border border-gray-500"></th>
            {dates.map((date, index) => (
              <th key={index} className="border border-gray-500 px-4 py-2">
                {date}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {modelIds.keys(restructuredData).map(modelId => (
            <tr key={modelId}>
              <td className="border border-gray-500 px-4 py-2">{modelId}</td>
              {dates.map(date => (
                <td key={date} className="border border-gray-500 px-4 py-2">
                  {restructuredData[modelId][date] || "NA"}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
