import React from 'react';
import axios from 'axios'; // Import Axios for HTTP requests

class Table extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      editingCell: null,
      cellValues: {}, // Track cell values
    };
  }
 

  // Function to handle cell click
  handleCellClick = (rowId, colId) => {
    this.setState({
      editingCell: { rowId, colId },
      // Initialize cell value if not present
      cellValues: {
        ...this.state.cellValues,
        [`${rowId}-${colId}`]: this.state.cellValues[`${rowId}-${colId}`] || '',
      },
    });
  };

  // Function to handle input change
  handleInputChange = (e) => {
    const { rowId, colId } = this.state.editingCell;
    const value = e.target.value;
    this.setState({
      cellValues: {
        ...this.state.cellValues,
        [`${rowId}-${colId}`]: value,
      },
    });
  };

  // Function to handle input blur
  handleInputBlur = () => {
    this.setState({ editingCell: null });
  };

  // Function to handle saving edited cell values
 // Function to handle saving edited cell values
 handleSave = async () => {
    const { editingCell, cellValues } = this.state;
    
    // Check if editingCell is not null
    if (editingCell) {
      const { rowId, colId } = editingCell;
      const value = cellValues[`${rowId}-${colId}`];
    
      try {
        // Retrieve the modelId from props
        const id = rowId;
        const modelId = this.props.modelIds[colIndex];
    
        // Make a PUT request to update the server data
        await axios.put(`http://localhost:8082/update/${id}/${modelId}`, {
          productionValue: value,
        });
        
        // Update the cellValues state to reflect the changes
        this.setState(prevState => ({
          cellValues: {
            ...prevState.cellValues,
            [`${rowId}-${colId}`]: value,
          },
          editingCell: null, // Clear editingCell after successful save
        }));
        
        console.log('Data updated successfully');
      } catch (error) {
        console.error('Error updating data:', error);
      }
    } else {
      console.error('Editing cell is null');
    }
  };
  
  
  render() {
    // Generate IDs 1 to 30
    const ids = Array.from({ length: 30 }, (_, i) => i + 1);
    // Sample ModelIds
 

    return (
      <div className="overflow-x-auto">
        <table className="table-auto border border-collapse border-gray-500">
          <thead>
            <tr className="bg-gray-200">
              <th className="border border-gray-500"></th>
              {/* Render IDs horizontally */}
              {ids.map((id) => (
                <th key={id} className="border border-gray-500 px-4 py-2">
                  {id}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {/* Render ModelIds vertically */}
            {this.props.modelIds.map((modelId, rowIndex) => (

              <tr key={modelId}>
                <td className="border border-gray-500 px-4 py-2">{modelId}</td>

                {ids.map((id, colIndex) => {
                  const cellKey = `${rowIndex}-${colIndex}`;
                  return (
                    <td
                      key={id}
                      className="border border-gray-500 px-4 py-2"
                      onClick={() => this.handleCellClick(rowIndex, colIndex)}
                    >
                      {/* Conditionally render input field */}
                      {this.state.editingCell &&
                      this.state.editingCell.rowId === rowIndex &&
                      this.state.editingCell.colId === colIndex ? (
                        <input
                          type="text"
                          value={this.state.cellValues[cellKey]}
                          onChange={this.handleInputChange}
                          onBlur={this.handleInputBlur}
                        />
                      ) : (
                        this.state.cellValues[cellKey]
                      )}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
        {/* Save button */}
        <button onClick={this.handleSave} className='py-10 border my-7 border-gray-500 px-6 py-0 '>Save</button>
      </div>
    );
  }
}

export default Table;
